-- 人事招聘
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('Android/ios 游戏开发工程师' , '北京' , '全职' ,  '若干', '1、负责Android/IOS手机平台游戏研发，与策划、美术、测试协同进行游戏制作；\n\n2、游戏引擎、开发工具等优化及制作。\n\n', '1、计算机（或相关专业）本科及以上学历，C++计算机基础知识扎实、理解能力强；\n\n2、熟练掌握cocos2d-x者优先,有良好的面向对象设计思想，了解设计模式；\n\n3、热爱游戏，并热爱游戏开发工作，拥有手机游戏开发经验者优先；\n\n4、具有良好的沟通能力和团队合作精神，工作责任心强；\n\n5、优秀的分析问题和解决问题的能力，对解决挑战性问题充满激情。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机游戏2D美术设计' , '北京' , '全职' ,  '若干', '1、负责手机游戏2D美术制作，包括人物、场景、UI、动画、特效等；\n\n2、与团队其他成员协作，完成游戏美术开发；\n\n3、负责项目相关宣传设计，海报，logo，宣传图等设计与制作。\n\n', '1、热爱游戏，有较强的手绘功底，精通PHOTOSHOP等绘图软件及绘图板使用；\n\n2、美术相关专业，大专及以上学历； \n\n3、具有良好的造型能力和丰富的想象力，能适应多种风格的绘画；有游戏UI制作经验；\n\n4、具有1年以上IOS及Android游戏制作经验者优先；\n\n5、有团队协作精神和优秀的人际沟通能力，能够快速融入团队；\n\n6、投递简历时请附带作品，否则视为无效简历。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机游戏3D美术设计' , '北京' , '全职' ,  '若干', '1、根据需求确定项目的美术风格，制定美术制作计划及规范 ；\n\n2、整体美术风格及美术质量把控 ；\n\n3、美术汇总拼接，美术资源整理，归档 ；\n\n4、美术资源优化 ；\n\n5、日常工作任务分配，进度控制，及质量审核，为其他美术成员提供必要支持。\n\n', '1、富有想象力 ；\n\n2、工作认真负责，有团队协作与奉献精神 ；\n\n3、熟练使用Unity3D及其常用编辑器 ；\n\n4、擅长清新亮丽，活泼可爱的欧美卡通风格 ；\n\n5、熟悉3D手游完整美术制作流程，擅长Unity环境下美术资源的优化和质量控制 ；\n\n6、三年以上游戏美术工作经验，至少在一款3D手游上线项目中担任主美术直至此项目上线（原画或3D 出身均可） 。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机单机游戏策划师' , '北京' , '全职' ,  '若干', '1、根据手机单机游戏市场及目标消费群体需求，制作产品提案；\n\n2、负责手机单机游戏策划案撰写、关卡的设计、脚本编写等工作；\n\n3、在游戏制作过程中，负责与程序、美术、运营等相关人员协调，确保游戏方案能在各部门正确实施；\n\n4、关注游戏的细节，追求最高品质画面、音效、UI设计、AI及游戏平衡性等。\n\n', '1、本科及以上学历；\n\n2、对基础的程序开发有了解，对美术设计有很好的画面感，逻辑能力；\n\n3、具备良好的文字功底和逻辑思维能力；熟悉office办公软件，具有优秀的文档撰写能力；\n\n4、喜爱游戏，熟悉各种游戏，包括：电视游戏和掌机游戏。对游戏有自己的感悟和认知；对时尚元素有很强的感知能力，了解动画、漫画、电影等；\n\n5、具备独立解决问题的能力和方法，有工作热情，能够承担较大的工作压力。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机网络游戏策划师' , '北京' , '全职' ,  '若干', '1、根据手机游戏市场及目标消费群体需求，制作产品提案； \n\n2、负责手机游戏策划案撰写,游戏数值编写，游戏数值、关卡的设计、脚本编写等工作； \n\n3、在游戏制作过程中，负责与程序、美术、运营等相关人员协调，确保游戏方案能在各部门正确实施； \n\n4、关注游戏的细节，追求最高品质画面、音效、UI设计、AI及游戏平衡性等；\n\n5、制定游戏玩法、系统以及系统之间的关联。\n\n', '1、本科及以上学历；\n\n2、对基础的程序开发有了解，对美术设计有很好的画面感，逻辑能力；\n\n3、具备良好的文字功底和逻辑思维能力；熟悉office办公软件，具有优秀的文档撰写能力；\n\n4、喜爱游戏，熟悉各种游戏，包括：电视游戏和掌机游戏。对游戏有自己的感悟和认知；对时尚元素有很强的感知能力，了解动画、漫画、电影等；\n\n5、具备独立解决问题的能力和方法，有工作热情，能够承担较大的工作压力。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机游戏美术设计' , '北京' , '全职' ,  '若干', '1、负责手机游戏美术制作，包括人物、场景、UI、动画、特效等；\n\n2、与团队其他成员协作，完成游戏美术开发；\n\n3、 负责项目相关宣传设计，海报，logo，宣传图等设计与制作。\n\n', '1、热爱游戏，有较强的手绘功底，熟悉flash,精通photoshop等绘图软件及绘图板使用。\n\n2、美术相关专业，大专及以上学历，男女不限；\n\n3、具有良好的造型能力和丰富的想象力，能适应多种风格的绘画；有游戏UI制作经验;\n\n4、具有1年以上游戏制作经验者优先；\n\n5、有团队协作精神和优秀的人际沟通能力，能够快速融入团队;\n\n6、 投递简历时务必附带作品，否则将视为无效简历。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机游戏测试' , '北京' , '全职' ,  '若干', '1.了解游戏功能，设计测试用例，实施游戏测试；\n\n2.记录Bug现象、产生步骤并提交研发部门；\n\n3.体验测试，积极提出优化建议；\n\n4.维护游戏测试文档，定期提交相关报告。\n\n', '1.熟练使用Android/IOS操作系统，对各类游戏有一定了解；\n\n2.熟练使用Office办公软件，具备良好的文档表述能力；\n\n3.有责任心，团队精神，乐于沟通交流；\n\n4.具备良好的问题分析和总结能力；\n\n5.U3D手游测试经验者优先。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('服务器端开发工程师' , '北京' , '全职' ,  '若干', '1、根据需求按计划完成开发工作；\n\n2、参与系统的需求调研和需求分析，撰写相关技术文档；\n\n3、搭建系统开发环境，完成系统框架和核心代码的实现。\n\n', '1、熟悉数据库，2年以上java开发经验；\n\n2、一年以上游戏服务器开发和运维经验；\n\n3、游戏玩家，并热爱游戏开发工作；\n\n4、本科及以上学历，计算机相关专业。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机游戏Unity3D主程序' , '北京' , '全职' ,  '若干', '1、根据需求制定项目的技术开发计划及规范，搭建项目框架 ；\n\n2、主逻辑担当 ；\n\n3、shader开发 ；\n\n4、架构及性能的深度优化 ；\n\n5、技术攻关 ；\n\n6、日常工作任务分配，进度控制，及代码审核，为其他程序员提供技术支持。\n\n', '1、逻辑清晰，思维严谨 ，计算机相关专业毕业；\n\n2、工作认真负责，有团队协作与奉献精神 ；\n\n3、熟练使用C#，C&C++扎实 ；\n\n4、熟悉游戏常用算法和U3D的shader编程 ；\n\n5、熟练使用Unity3D,NGUI,熟悉IOS开发环境 ；\n\n6、数学和图形学基础扎实，熟悉OpenGL ；\n\n7、熟悉主流软件开发模式和流程管理 ， \n\n8、两年以上游戏研发经验，一年以上U3D手游研发经验，在至少一款U3D手游上线项目中担任架构，主程或Senior Programmer等核心技术成员直至此项目上线。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机游戏运营专员' , '北京' , '全职' ,  '若干', '1.负责手机网游在各大渠道的日常运营计划制定、执行、监管与分析;\n\n2.负责运营数据分析，并根据数据变化提供运营建议； \n\n3.与开发人员进行交流，提出运营建议，改进和完善游戏； \n\n4.掌握竞争对手产品改进及运营方式动态。 \n\n', '1. 本科及以上学历；\n\n2. 对手机网络游戏产品及用户具有深入理解;\n\n3. 在运营、市场推广方面有自己的独到见解;\n\n4. 有较强的数据分析与逻辑分析能力，并具备良好的文案写作能力;\n\n5. 工作积极主动，有强烈的工作愿望与责任心，有包容不同意见的工作态度。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机游戏评测专员' , '北京' , '全职' ,  '若干', '1、对公司引进的游戏产品和市场曝光的重点产品进行综合评估与分析；\n\n2、从游戏策划、美术、操作、题材、市场、玩法、功能、系统等方面全方位分析游戏优缺点并撰写评估报告；\n\n3、全面、客观、准确的拆解与分析业内成功的游戏产品，并形成文档；\n\n4、对合作的游戏提出可行性修改建议，包括研发和运营、推广层面。；\n\n5、跟踪市场新曝光的产品信息，第一时间了解市场上的游戏产品动态，筛选优质产品，完成推荐报告。\n\n', '1.专科及以上学历，专业不限；\n\n2.游戏爱好者，有丰富的游戏经验和深厚的游戏理解，要求游戏经验5年以上；\n\n3.有业内工作经验，参加过游戏测评，或者系统完整的参与过一款游戏的开发历程；\n\n4.对手游有极大的热情，深入了解过一款或多款手游，并拥有自己独到的见解；\n\n5.良好的文字功底和文表图呈现技巧，擅长使用Word、Excel等工作软件；\n\n6.积极、乐观、低调、务实，善于团队协作，能够承受一定的工作压力；\n\n7.精通一门或者多门外语优先。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('商务合作专员' , '北京' , '全职' ,  '若干', '1.了解并分析国内手机游戏开发团队和产品情况，选择优质产品合作；\n\n2.负责合作手机游戏产品的合作伙伴关系维护；\n\n3.负责合作的游戏产品从评估到上线运营过程中的沟通协调；\n\n4.负责与合作厂商的结算工作；\n\n5.领导交代的其他任务。\n\n', '1、年龄要求：22-30；性别不限，学历要求：专科及以上； \n\n2、拥有手机游戏市场、商务、渠道相关工作经验者优先；\n\n3、了解移动互联网领域的各种商业模式，熟悉中国手机游戏市场的现状和发展，具备独立的商务拓展能力；\n\n4、工作态度认真，踏实严谨，积极主动，能承受工作压力，吃苦耐劳；\n\n5、具有执行力、优秀的团队合作精神和敬业精神。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('商务专员' , '北京' , '全职' ,  '若干', '1、负责手机游戏相关渠道合作伙伴的开拓、洽谈，游戏接入、运营及推广等工作；\n\n2、负责手机游戏产品在相关渠道的数据分析及运营活动的开展；\n\n3、 维护渠道合作伙伴关系，定期沟通、协调、处理相关事宜；\n\n4、负责渠道信息的跟踪搜集，定期进行业务状况分析，提出产品开发和改进的建议。\n\n', '1、年龄22岁以上（应届毕业生），性别不限；\n\n2、本科及以上学历，熟练操作各种办公软件，比如：WORD,PPT,EXCEL等；\n\n3、良好的职业素养与形象气质，具有较强的沟通能力，能独立与合作伙伴进行业务沟通；\n\n4、具有团队合作精神，能承担较高的工作压力，工作态度认真，踏实严谨，积极主动；\n\n5、熟悉手机游戏的运营、推广相关工作者优先。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('视频制作经理' , '北京' , '全职' ,  '若干', '1、热爱游戏事业，对于游戏有深刻的认识；\n\n2、熟练掌握游戏宣传视频的全部操作流程与整体编排；\n\n3、能独立完成选题、撰稿、组织拍摄、后期剪辑等相关工作；\n\n4、熟悉各类视频拍摄、编辑制作方法，完成后期剪辑；\n\n5、富于创新、逻辑性强，具备较强文字功底。\n\n', '1、具有优秀的视频编辑技术，熟练使用各类制作视频软件；\n\n2、具有较强的文字撰稿能力、视频的策划能力和创新能力,善于沟通与团队协作；\n\n3、有2年以上视频节目或视频策划制作相关经验；\n\n4、应聘简历需附个人作品或作品链接。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('市场高级经理' , '北京' , '全职' ,  '若干', '1、热爱游戏，对于游戏有深刻的认识; 针对游戏产品制订市场和公关推广方案计划，执行推广并进行控制；\n\n2、达成游戏下载量、用户量、活跃用户量和市场营收等推广指标；\n\n3、对移动游戏市场活动、公关宣传、竞争与用户有定期分析和评价；\n\n4、积极寻求移动市场推广资源，负责对外与市场资源合作，寻找合作切入点，拓展合作渠道。\n\n', '1、3年以上游戏推广经验，有营销策划能力，具有成功游戏推广工作经验优先；\n\n2、移动市场数据敏感度高，密切跟踪移动互联网市场资源，有较好的数据处理能力和缜密的逻辑分析能力；\n\n3、熟悉微信、微博、IM、贴吧的营销推广模式；\n\n4、优秀的文案能力和团队协作能力。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('设计经理' , '北京' , '全职' ,  '若干', '1、热爱游戏，了解公司和游戏产品的设计风格及发展方向；\n\n2、负责对外宣传的平面设计工作，游戏开服图片创意设计包括报纸、杂志、网络、户外、框架、展板等；\n\n3、负责对外宣传的产品FLASH网络广告创意设计，包括游戏产品推广弹窗和点击图片等；\n\n4、负责公司网站及游戏产品的设计、维护的创意设计。\n\n', '1、熟练平面设计及动画设计等相关工具的使用；\n\n2、有丰富设计工作经验，有创新能力，有较强的美学素质和审美能力；\n\n3、美术设计相关专业毕业者优先；\n\n4、应聘简历需附个人作品或作品链接。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('市场专员' , '北京' , '全职' ,  '若干', '1、负责日常媒体关系的拓展与维护，与媒体进行沟通；\n\n2、日常广告投放的联系沟通、广告实施数据的分析以及媒体合作活动的实施、跟进；\n\n3、负责公司相关展会活动的策划、执行、跟进；\n\n4、负责调查和了解目前游戏市场运营和推广情况，并进行产品分析和整理。\n\n', '1、本科及以上学历，熟练操作各种办公软件，比如：WORD,PPT,EXCEL等；\n\n2、性格开朗，较强的人际关系维护与沟通能力，社交能力突出；\n\n3、能快速熟悉并了解国内游戏媒体(网络媒体方向)的传播习惯，游戏媒体资源丰富者优先；\n\n4、具有团队合作精神，能承担较高的工作压力；工作态度认真，踏实严谨，积极主动。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('广告高级经理' , '北京' , '全职' ,  '若干', '1、热爱游戏，独立完成游戏广告投放的策略制定与执行、出价调价、推广预算与成本控制；\n\n2、媒介执行的质量把控；广告素材的协调与优化;广告投放效果分析及改进；\n\n3、独立制作数据分析报告，对数据要有高度的敏感性。\n\n', '1、具备严谨而有效的网络广告投放分析思路，对网络广告投放数据有长期的分析经验；\n\n2、专科以上学历，熟悉网络广告投放的工作流程，具有基于效果营销的市场推广购买经验（CPM、CPC、CPA及其他）；\n\n3、具有优秀的沟通能力，学习能力强，逻辑清晰; 具有高度的工作积极性和责任心。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机游戏产品经理' , '北京' , '全职' ,  '若干', '1、负责跟进公司代理发行的手机网游，对CP的产品提出改进建议；\n\n2、负责游戏的运营策略及运营活动的制定、执行、监管与分析；\n\n3、和技术部门、市场部门、客服部门协调工作；\n\n4、跟进CP迭代开发计划，负责版本更新相关工作组织和实施；\n\n5、分析运营数据、分析竞争产品，不断改进产品和运营提升收入；\n\n6、产品运营过程中的突发事件的决策和处理；\n\n7、安排跟踪项目组内人员工作。\n\n', '1、本科以上学历，2年以上手机网游运营经验；\n\n2、有500万月收入以上的手机游戏主运营经验6个月以上;\n\n3、具有超强执行力和工作热情，可以带领团队;\n\n4、是游戏狂热分子，熟悉市场主流中重度手机游戏。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('手机游戏产品专员' , '北京' , '全职' ,  '若干', '1、对公司引进的游戏产品和市场曝光的重点产品进行综合评估与分析；\n\n2、从游戏策划、美术、操作、题材、市场、玩法、功能、系统等方面全方位分析游戏优缺点并撰写评估报告；\n\n3、全面、客观、准确的拆解与分析业内成功的游戏产品，并形成文档；\n\n4、对合作的游戏提出可行性修改建议，包括研发和运营、推广层面；\n\n5、跟踪市场新曝光的产品信息，第一时间了解市场上的游戏产品动态，筛选优质产品，完成推荐报告。\n\n', '1.专科及以上学历，专业不限；\n\n2.游戏爱好者，有丰富的游戏经验和深厚的游戏理解，要求游戏经验5年以上；\n\n3.有业内工作经验，参加过游戏测评，或者系统完整的参与过一款游戏的开发历程；\n\n4.对手游有极大的热情，深入了解过一款或多款手游，并拥有自己独到的见解；\n\n5.良好的文字功底和文表图呈现技巧，擅长使用Word、Excel等工作软件；\n\n6.积极、乐观、低调、务实，善于团队协作，能够承受一定的工作压力；\n\n7.精通一门或者多门外语优先。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('投资总监' , '北京' , '全职' ,  '若干', '1、手游行业潜力项目的挖掘寻找，并建立联系，深度沟通；\n\n2、手游行业靠谱创始人和团队的挖掘寻找，并建立联系，深度沟通；\n\n3、手游产品和项目的评估，并形成评估报告，提出决策建议；\n\n4、手游行业的深入研究和分析；\n\n5、手游圈子人脉的搭建和维护；\n\n6、参与项目投后管理工作。\n\n', '1、热衷于手游行业，对优秀的手游产品有感觉；\n\n2、有广泛的手游人脉关系，并有一定影响力；\n\n3、游戏行业3年以上从业经历、手游行业1年以上从业经历；\n\n4、有创业经历或投资经验者优先；\n\n5、工作主动积极，有独立完成工作的能力，适应出差；\n\n6、较强的沟通能力和学习能力；\n\n7、关注行业发展和变化，有兴趣并且善于研究分析用户体验和行为；\n\n8、善用PPT。\n\n', NULL );
INSERT INTO RECRUITS (RECRUIT_POSITION, WORK_PLACE, WORK_PROPERTY, RECRUIT_NUM, RESPONSIBILITIES, REQUIREMENTS, OTHERS )
  VALUES ('投资经理' , '北京' , '全职' ,  '若干', '1、发现优秀的游戏项目和创业团队；\n\n2、与团队建立联系，良好沟通，并持续跟进；\n\n3、收集整理资料，完成基本的项目评估报告，并提出个人见解；\n\n4、负责项目的财务和商业的尽职调查和项目执行；\n\n5、跟踪国内外手游行业发展，并加以分析，提出投资关注方向；\n\n6、参与项目投资后管理工作。\n\n', '1、细心，严谨，认真，谦逊；\n\n2、性格开朗，善于沟通，善于协调；\n\n3、对手游行业有基本了解，有一定行业人脉；\n\n4、对投资行业有浓厚兴趣并乐于投身其中；\n\n5、较强的沟通能力和学习能力；\n\n6、熟练使用基本office办公软件，善用PPT；\n\n7、关注行业发展和变化，有兴趣并且善于研究分析用户体验和行为。\n\n', NULL );
